
-- =============================================
-- CONSOLIDATED QUIZ QUESTIONS SEED
-- Combined from: crypto, web3, security, defi, advanced
-- =============================================

INSERT INTO game_questions (category, difficulty, question_text, options, correct_index, explanation, from_course) VALUES

-- 1. CRYPTO 101
('Crypto 101', 'easy', '{"en": "Who created Bitcoin?", "es": "¿Quién creó Bitcoin?"}', '[{"en": "Vitalik Buterin", "es": "Vitalik Buterin"}, {"en": "Satoshi Nakamoto", "es": "Satoshi Nakamoto"}, {"en": "Elon Musk", "es": "Elon Musk"}, {"en": "Mark Zuckerberg", "es": "Mark Zuckerberg"}]', 1, '{"en": "Bitcoin was created by the pseudonymous Satoshi Nakamoto, who published the whitepaper in 2008 and launched the network in 2009.", "es": "Bitcoin fue creado por el pseudónimo Satoshi Nakamoto, quien publicó el whitepaper en 2008 y lanzó la red en 2009."}', 'crypto-101'),
('Crypto 101', 'easy', '{"en": "What is the maximum supply of Bitcoin?", "es": "¿Cuál es el suministro máximo de Bitcoin?"}', '[{"en": "10 million", "es": "10 millones"}, {"en": "21 million", "es": "21 millones"}, {"en": "100 million", "es": "100 millones"}, {"en": "Unlimited", "es": "Ilimitado"}]', 1, '{"en": "Bitcoin has a hard cap of 21 million coins that will ever exist, making it a deflationary asset.", "es": "Bitcoin tiene un límite duro de 21 millones de monedas que existirán, haciéndolo un activo deflacionario."}', 'crypto-101'),
('Crypto 101', 'easy', '{"en": "What is Ethereum primarily known for?", "es": "¿Por qué es principalmente conocido Ethereum?"}', '[{"en": "Being a digital gold", "es": "Ser oro digital"}, {"en": "Smart contracts and dApps", "es": "Contratos inteligentes y dApps"}, {"en": "Fast payments", "es": "Pagos rápidos"}, {"en": "Privacy transactions", "es": "Transacciones privadas"}]', 1, '{"en": "Ethereum is primarily known for enabling smart contracts and decentralized applications (dApps) on its blockchain.", "es": "Ethereum es principalmente conocido por habilitar contratos inteligentes y aplicaciones descentralizadas (dApps) en su blockchain."}', 'crypto-101'),
('Crypto 101', 'easy', '{"en": "What is a stablecoin?", "es": "¿Qué es una stablecoin?"}', '[{"en": "A coin that never changes value", "es": "Una moneda que nunca cambia de valor"}, {"en": "A crypto pegged to a stable asset like USD", "es": "Una crypto vinculada a un activo estable como USD"}, {"en": "A physical coin", "es": "Una moneda física"}, {"en": "A mining reward", "es": "Una recompensa de minería"}]', 1, '{"en": "A stablecoin is a cryptocurrency designed to maintain a stable value by being pegged to a fiat currency or other assets.", "es": "Una stablecoin es una criptomoneda diseñada para mantener un valor estable al estar vinculada a una moneda fiat u otros activos."}', 'crypto-101'),
('Crypto 101', 'easy', '{"en": "What is altcoin?", "es": "¿Qué es una altcoin?"}', '[{"en": "Another name for Bitcoin", "es": "Otro nombre para Bitcoin"}, {"en": "Any cryptocurrency other than Bitcoin", "es": "Cualquier criptomoneda que no sea Bitcoin"}, {"en": "A fake cryptocurrency", "es": "Una criptomoneda falsa"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}]', 1, '{"en": "Altcoin refers to any cryptocurrency other than Bitcoin. It comes from alternative coin.", "es": "Altcoin se refiere a cualquier criptomoneda que no sea Bitcoin. Viene de moneda alternativa."}', 'crypto-101'),
('Crypto 101', 'medium', '{"en": "What is the halving in Bitcoin?", "es": "¿Qué es el halving en Bitcoin?"}', '[{"en": "Splitting Bitcoin in half", "es": "Dividir Bitcoin a la mitad"}, {"en": "When mining rewards are cut in half every ~4 years", "es": "Cuando las recompensas de minería se reducen a la mitad cada ~4 años"}, {"en": "A 50% price drop", "es": "Una caída de precio del 50%"}, {"en": "A security feature", "es": "Una característica de seguridad"}]', 1, '{"en": "The Bitcoin halving occurs approximately every 4 years (210,000 blocks), cutting the block reward in half to control inflation.", "es": "El halving de Bitcoin ocurre aproximadamente cada 4 años (210,000 bloques), reduciendo la recompensa del bloque a la mitad para controlar la inflación."}', 'crypto-101'),
('Crypto 101', 'medium', '{"en": "What is market cap in cryptocurrency?", "es": "¿Qué es la capitalización de mercado en criptomonedas?"}', '[{"en": "The maximum price a coin can reach", "es": "El precio máximo que una moneda puede alcanzar"}, {"en": "Total value of all coins in circulation", "es": "Valor total de todas las monedas en circulación"}, {"en": "The trading volume", "es": "El volumen de trading"}, {"en": "The number of holders", "es": "El número de poseedores"}]', 1, '{"en": "Market cap is calculated by multiplying the current price by the total circulating supply. It represents the total value of a cryptocurrency.", "es": "La capitalización de mercado se calcula multiplicando el precio actual por el suministro circulante total. Representa el valor total de una criptomoneda."}', 'crypto-101'),
('Crypto 101', 'medium', '{"en": "What does HODL mean?", "es": "¿Qué significa HODL?"}', '[{"en": "A trading strategy", "es": "Una estrategia de trading"}, {"en": "Hold On for Dear Life - long-term holding", "es": "Hold On for Dear Life - mantener a largo plazo"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A mining method", "es": "Un método de minería"}]', 1, '{"en": "HODL originated from a typo of hold and became a meme meaning to hold through volatility. It is often interpreted as Hold On for Dear Life.", "es": "HODL se originó de un error tipográfico de hold y se convirtió en un meme que significa mantener a través de la volatilidad. A menudo se interpreta como Hold On for Dear Life."}', 'crypto-101'),
('Crypto 101', 'medium', '{"en": "What is a token burn?", "es": "¿Qué es una quema de tokens?"}', '[{"en": "Physically destroying coins", "es": "Destruir físicamente monedas"}, {"en": "Permanently removing tokens from circulation", "es": "Eliminar permanentemente tokens de circulación"}, {"en": "A type of attack", "es": "Un tipo de ataque"}, {"en": "A wallet feature", "es": "Una característica de billetera"}]', 1, '{"en": "Token burning permanently removes tokens from circulation by sending them to an inaccessible address, reducing supply.", "es": "La quema de tokens elimina permanentemente tokens de circulación enviándolos a una dirección inaccesible, reduciendo el suministro."}', 'crypto-101'),
('Crypto 101', 'medium', '{"en": "What is a CEX?", "es": "¿Qué es un CEX?"}', '[{"en": "A decentralized exchange", "es": "Un exchange descentralizado"}, {"en": "A centralized exchange like Coinbase or Binance", "es": "Un exchange centralizado como Coinbase o Binance"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A wallet provider", "es": "Un proveedor de billeteras"}]', 1, '{"en": "A CEX (Centralized Exchange) is a trading platform operated by a company that holds custody of user funds, like Coinbase or Binance.", "es": "Un CEX (Exchange Centralizado) es una plataforma de trading operada por una empresa que tiene custodia de los fondos de usuarios, como Coinbase o Binance."}', 'crypto-101'),
('Crypto 101', 'hard', '{"en": "What is the difference between ERC-20 and ERC-721?", "es": "¿Cuál es la diferencia entre ERC-20 y ERC-721?"}', '[{"en": "ERC-721 is faster", "es": "ERC-721 es más rápido"}, {"en": "ERC-20 is fungible, ERC-721 is non-fungible (NFT)", "es": "ERC-20 es fungible, ERC-721 es no fungible (NFT)"}, {"en": "They are the same", "es": "Son iguales"}, {"en": "ERC-20 is for Ethereum, ERC-721 for Bitcoin", "es": "ERC-20 es para Ethereum, ERC-721 para Bitcoin"}]', 1, '{"en": "ERC-20 tokens are fungible (interchangeable, like currencies), while ERC-721 tokens are non-fungible (unique, like NFTs).", "es": "Los tokens ERC-20 son fungibles (intercambiables, como monedas), mientras que los tokens ERC-721 son no fungibles (únicos, como NFTs)."}', 'crypto-101'),
('Crypto 101', 'hard', '{"en": "What is the UTXO model?", "es": "¿Qué es el modelo UTXO?"}', '[{"en": "A type of wallet", "es": "Un tipo de billetera"}, {"en": "Unspent Transaction Output model used by Bitcoin", "es": "Modelo de Salida de Transacción No Gastada usado por Bitcoin"}, {"en": "A consensus mechanism", "es": "Un mecanismo de consenso"}, {"en": "A smart contract system", "es": "Un sistema de contratos inteligentes"}]', 1, '{"en": "UTXO (Unspent Transaction Output) is the accounting model Bitcoin uses, where each transaction consumes previous outputs and creates new ones.", "es": "UTXO (Salida de Transacción No Gastada) es el modelo contable que usa Bitcoin, donde cada transacción consume salidas anteriores y crea nuevas."}', 'crypto-101'),
('Crypto 101', 'hard', '{"en": "What is a hard fork?", "es": "¿Qué es un hard fork?"}', '[{"en": "A wallet backup", "es": "Un respaldo de billetera"}, {"en": "A permanent blockchain split creating a new chain", "es": "Una división permanente de blockchain creando una nueva cadena"}, {"en": "A mining pool", "es": "Un pool de minería"}, {"en": "A type of token", "es": "Un tipo de token"}]', 1, '{"en": "A hard fork is a permanent divergence in the blockchain, creating a new chain with different rules. Bitcoin Cash is a hard fork of Bitcoin.", "es": "Un hard fork es una divergencia permanente en la blockchain, creando una nueva cadena con reglas diferentes. Bitcoin Cash es un hard fork de Bitcoin."}', 'crypto-101'),
('Crypto 101', 'hard', '{"en": "What is Proof of Stake (PoS)?", "es": "¿Qué es Prueba de Participación (PoS)?"}', '[{"en": "Mining with computers", "es": "Minar con computadoras"}, {"en": "Consensus where validators stake tokens to secure the network", "es": "Consenso donde validadores apuestan tokens para asegurar la red"}, {"en": "A type of stablecoin", "es": "Un tipo de stablecoin"}, {"en": "A wallet feature", "es": "Una característica de billetera"}]', 1, '{"en": "Proof of Stake is a consensus mechanism where validators lock up tokens as collateral to validate transactions and earn rewards.", "es": "Prueba de Participación es un mecanismo de consenso donde los validadores bloquean tokens como colateral para validar transacciones y ganar recompensas."}', 'crypto-101'),
('Crypto 101', 'hard', '{"en": "What is atomic swap?", "es": "¿Qué es un intercambio atómico?"}', '[{"en": "A fast trading strategy", "es": "Una estrategia de trading rápida"}, {"en": "Trustless exchange between two different blockchains", "es": "Intercambio sin confianza entre dos blockchains diferentes"}, {"en": "A type of NFT", "es": "Un tipo de NFT"}, {"en": "A wallet backup", "es": "Un respaldo de billetera"}]', 1, '{"en": "Atomic swaps enable direct, trustless cryptocurrency exchanges between different blockchains without intermediaries using hash time-locked contracts.", "es": "Los intercambios atómicos permiten intercambios directos y sin confianza de criptomonedas entre diferentes blockchains sin intermediarios usando contratos con bloqueo temporal hash."}', 'crypto-101'),

-- 2. WEB3 BASICS
('Web3 Basics', 'easy', '{"en": "What does Web3 primarily refer to?", "es": "¿A qué se refiere principalmente Web3?"}', '[{"en": "A new version of web browsers", "es": "Una nueva versión de navegadores web"}, {"en": "The decentralized internet powered by blockchain", "es": "El internet descentralizado impulsado por blockchain"}, {"en": "A social media platform", "es": "Una plataforma de redes sociales"}, {"en": "A programming language", "es": "Un lenguaje de programación"}]', 1, '{"en": "Web3 refers to the vision of a decentralized internet where users own their data and digital assets through blockchain technology.", "es": "Web3 se refiere a la visión de un internet descentralizado donde los usuarios son dueños de sus datos y activos digitales mediante tecnología blockchain."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "In Web3, who controls your data?", "es": "En Web3, ¿quién controla tus datos?"}', '[{"en": "Google and Facebook", "es": "Google y Facebook"}, {"en": "The government", "es": "El gobierno"}, {"en": "You, the user", "es": "Tú, el usuario"}, {"en": "Internet service providers", "es": "Proveedores de internet"}]', 2, '{"en": "In Web3, users have ownership and control over their own data through cryptographic keys and decentralized storage.", "es": "En Web3, los usuarios tienen propiedad y control sobre sus propios datos mediante llaves criptográficas y almacenamiento descentralizado."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a blockchain?", "es": "¿Qué es una blockchain?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A distributed ledger that records transactions", "es": "Un libro contable distribuido que registra transacciones"}, {"en": "A computer virus", "es": "Un virus informático"}, {"en": "A social network", "es": "Una red social"}]', 1, '{"en": "A blockchain is a distributed, immutable ledger that records transactions across many computers so the record cannot be altered retroactively.", "es": "Una blockchain es un libro contable distribuido e inmutable que registra transacciones en muchas computadoras para que el registro no pueda alterarse retroactivamente."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a crypto wallet?", "es": "¿Qué es una billetera crypto?"}', '[{"en": "A physical wallet for storing cash", "es": "Una billetera física para guardar efectivo"}, {"en": "A software that stores your private keys", "es": "Un software que almacena tus llaves privadas"}, {"en": "A bank account", "es": "Una cuenta bancaria"}, {"en": "A credit card", "es": "Una tarjeta de crédito"}]', 1, '{"en": "A crypto wallet is software or hardware that stores your private keys, allowing you to send, receive, and manage your cryptocurrencies.", "es": "Una billetera crypto es software o hardware que almacena tus llaves privadas, permitiéndote enviar, recibir y gestionar tus criptomonedas."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is the main difference between Web2 and Web3?", "es": "¿Cuál es la principal diferencia entre Web2 y Web3?"}', '[{"en": "Web3 is faster", "es": "Web3 es más rápido"}, {"en": "Web3 is decentralized while Web2 is centralized", "es": "Web3 es descentralizado mientras Web2 es centralizado"}, {"en": "Web3 has more social media", "es": "Web3 tiene más redes sociales"}, {"en": "There is no difference", "es": "No hay diferencia"}]', 1, '{"en": "The main difference is that Web2 relies on centralized platforms (like Google, Facebook) while Web3 uses decentralized protocols and gives users ownership.", "es": "La principal diferencia es que Web2 depende de plataformas centralizadas (como Google, Facebook) mientras Web3 usa protocolos descentralizados y da propiedad a los usuarios."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is decentralization?", "es": "¿Qué es la descentralización?"}', '[{"en": "Having one central authority control everything", "es": "Tener una autoridad central que controla todo"}, {"en": "Distributing control across many participants", "es": "Distribuir el control entre muchos participantes"}, {"en": "A type of internet connection", "es": "Un tipo de conexión a internet"}, {"en": "A programming technique", "es": "Una técnica de programación"}]', 1, '{"en": "Decentralization means distributing control, data, and decision-making across a network rather than having a single point of control.", "es": "Descentralización significa distribuir el control, datos y toma de decisiones a través de una red en lugar de tener un solo punto de control."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a smart contract?", "es": "¿Qué es un contrato inteligente?"}', '[{"en": "A legal document signed digitally", "es": "Un documento legal firmado digitalmente"}, {"en": "Self-executing code on the blockchain", "es": "Código auto-ejecutable en la blockchain"}, {"en": "An AI chatbot", "es": "Un chatbot de IA"}, {"en": "A mobile app", "es": "Una aplicación móvil"}]', 1, '{"en": "A smart contract is self-executing code stored on a blockchain that automatically enforces and executes the terms of an agreement.", "es": "Un contrato inteligente es código auto-ejecutable almacenado en una blockchain que automáticamente hace cumplir y ejecuta los términos de un acuerdo."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a private key?", "es": "¿Qué es una llave privada?"}', '[{"en": "A password for your email", "es": "Una contraseña para tu email"}, {"en": "A secret code that gives access to your crypto", "es": "Un código secreto que da acceso a tu crypto"}, {"en": "Your username", "es": "Tu nombre de usuario"}, {"en": "A physical key", "es": "Una llave física"}]', 1, '{"en": "A private key is a secret cryptographic code that proves ownership of your crypto assets and allows you to sign transactions.", "es": "Una llave privada es un código criptográfico secreto que prueba propiedad de tus activos crypto y te permite firmar transacciones."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a public key?", "es": "¿Qué es una llave pública?"}', '[{"en": "Your wallet address that others can see", "es": "Tu dirección de billetera que otros pueden ver"}, {"en": "Your private key", "es": "Tu llave privada"}, {"en": "A password", "es": "Una contraseña"}, {"en": "A government ID", "es": "Una identificación del gobierno"}]', 0, '{"en": "A public key (or wallet address) is derived from your private key and can be shared publicly for others to send you crypto.", "es": "Una llave pública (o dirección de billetera) se deriva de tu llave privada y puede compartirse públicamente para que otros te envíen crypto."}', 'web3-basics'),
('Web3 Basics', 'easy', '{"en": "What is a dApp?", "es": "¿Qué es una dApp?"}', '[{"en": "A mobile app from Google Play", "es": "Una app móvil de Google Play"}, {"en": "A decentralized application running on blockchain", "es": "Una aplicación descentralizada corriendo en blockchain"}, {"en": "A desktop program", "es": "Un programa de escritorio"}, {"en": "A web browser", "es": "Un navegador web"}]', 1, '{"en": "A dApp (decentralized application) is an application that runs on a blockchain network rather than centralized servers.", "es": "Una dApp (aplicación descentralizada) es una aplicación que corre en una red blockchain en lugar de servidores centralizados."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What consensus mechanism does Bitcoin use?", "es": "¿Qué mecanismo de consenso usa Bitcoin?"}', '[{"en": "Proof of Stake", "es": "Prueba de Participación"}, {"en": "Proof of Work", "es": "Prueba de Trabajo"}, {"en": "Delegated Proof of Stake", "es": "Prueba de Participación Delegada"}, {"en": "Proof of Authority", "es": "Prueba de Autoridad"}]', 1, '{"en": "Bitcoin uses Proof of Work (PoW), where miners compete to solve complex mathematical puzzles to validate transactions and create new blocks.", "es": "Bitcoin usa Prueba de Trabajo (PoW), donde los mineros compiten para resolver puzzles matemáticos complejos para validar transacciones y crear nuevos bloques."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is gas in Ethereum?", "es": "¿Qué es el gas en Ethereum?"}', '[{"en": "Fuel for cars", "es": "Combustible para autos"}, {"en": "The fee paid to process transactions", "es": "La tarifa pagada para procesar transacciones"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A mining reward", "es": "Una recompensa de minería"}]', 1, '{"en": "Gas is the unit measuring the computational effort required to execute operations on Ethereum. Users pay gas fees to compensate miners/validators.", "es": "Gas es la unidad que mide el esfuerzo computacional requerido para ejecutar operaciones en Ethereum. Los usuarios pagan tarifas de gas para compensar a los mineros/validadores."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is a seed phrase?", "es": "¿Qué es una frase semilla?"}', '[{"en": "A password for websites", "es": "Una contraseña para sitios web"}, {"en": "12-24 words that can recover your wallet", "es": "12-24 palabras que pueden recuperar tu billetera"}, {"en": "A username", "es": "Un nombre de usuario"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}]', 1, '{"en": "A seed phrase (recovery phrase) is a series of 12-24 words that can be used to recover access to your crypto wallet if you lose your device.", "es": "Una frase semilla (frase de recuperación) es una serie de 12-24 palabras que pueden usarse para recuperar acceso a tu billetera crypto si pierdes tu dispositivo."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is a DAO?", "es": "¿Qué es una DAO?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A decentralized autonomous organization", "es": "Una organización autónoma descentralizada"}, {"en": "A mining pool", "es": "Un pool de minería"}, {"en": "A web browser", "es": "Un navegador web"}]', 1, '{"en": "A DAO (Decentralized Autonomous Organization) is an organization governed by smart contracts and token-holder voting rather than traditional management.", "es": "Una DAO (Organización Autónoma Descentralizada) es una organización gobernada por contratos inteligentes y votación de poseedores de tokens en lugar de administración tradicional."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is the purpose of a hash in blockchain?", "es": "¿Cuál es el propósito de un hash en blockchain?"}', '[{"en": "To encrypt messages", "es": "Para encriptar mensajes"}, {"en": "To create a unique fingerprint of data", "es": "Para crear una huella digital única de datos"}, {"en": "To mine cryptocurrency", "es": "Para minar criptomonedas"}, {"en": "To send emails", "es": "Para enviar correos"}]', 1, '{"en": "A hash creates a unique fixed-length fingerprint of any data. In blockchain, it links blocks together and ensures data integrity.", "es": "Un hash crea una huella digital única de longitud fija de cualquier dato. En blockchain, enlaza bloques y asegura la integridad de datos."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is a node in blockchain?", "es": "¿Qué es un nodo en blockchain?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A computer that maintains a copy of the blockchain", "es": "Una computadora que mantiene una copia de la blockchain"}, {"en": "A smart contract", "es": "Un contrato inteligente"}, {"en": "A wallet address", "es": "Una dirección de billetera"}]', 1, '{"en": "A node is a computer that maintains a copy of the blockchain and helps validate and relay transactions across the network.", "es": "Un nodo es una computadora que mantiene una copia de la blockchain y ayuda a validar y transmitir transacciones a través de la red."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What does immutability mean in blockchain?", "es": "¿Qué significa inmutabilidad en blockchain?"}', '[{"en": "Data can be easily changed", "es": "Los datos pueden cambiarse fácilmente"}, {"en": "Once data is recorded, it cannot be altered", "es": "Una vez que los datos se registran, no pueden alterarse"}, {"en": "Data is encrypted", "es": "Los datos están encriptados"}, {"en": "Data is private", "es": "Los datos son privados"}]', 1, '{"en": "Immutability means that once data is written to the blockchain, it cannot be altered or deleted. This creates a permanent, tamper-proof record.", "es": "Inmutabilidad significa que una vez que los datos se escriben en la blockchain, no pueden alterarse o eliminarse. Esto crea un registro permanente a prueba de manipulación."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is the difference between Layer 1 and Layer 2?", "es": "¿Cuál es la diferencia entre Capa 1 y Capa 2?"}', '[{"en": "Layer 2 is slower than Layer 1", "es": "Capa 2 es más lenta que Capa 1"}, {"en": "Layer 2 is built on top of Layer 1 for scalability", "es": "Capa 2 se construye sobre Capa 1 para escalabilidad"}, {"en": "They are the same", "es": "Son iguales"}, {"en": "Layer 1 is a wallet", "es": "Capa 1 es una billetera"}]', 1, '{"en": "Layer 1 is the base blockchain (like Ethereum). Layer 2 solutions (like Polygon, Arbitrum) are built on top to increase speed and reduce costs.", "es": "Capa 1 es la blockchain base (como Ethereum). Las soluciones de Capa 2 (como Polygon, Arbitrum) se construyen encima para aumentar velocidad y reducir costos."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is an NFT?", "es": "¿Qué es un NFT?"}', '[{"en": "A type of cryptocurrency like Bitcoin", "es": "Un tipo de criptomoneda como Bitcoin"}, {"en": "A unique digital token representing ownership", "es": "Un token digital único que representa propiedad"}, {"en": "A blockchain network", "es": "Una red blockchain"}, {"en": "A wallet application", "es": "Una aplicación de billetera"}]', 1, '{"en": "An NFT (Non-Fungible Token) is a unique digital token that represents ownership of a specific item like art, music, or collectibles.", "es": "Un NFT (Token No Fungible) es un token digital único que representa propiedad de un artículo específico como arte, música o coleccionables."}', 'web3-basics'),
('Web3 Basics', 'medium', '{"en": "What is DeFi?", "es": "¿Qué es DeFi?"}', '[{"en": "A social media platform", "es": "Una plataforma de redes sociales"}, {"en": "Decentralized finance services on blockchain", "es": "Servicios financieros descentralizados en blockchain"}, {"en": "A cryptocurrency exchange", "es": "Un exchange de criptomonedas"}, {"en": "A mining pool", "es": "Un pool de minería"}]', 1, '{"en": "DeFi (Decentralized Finance) refers to financial services built on blockchain that operate without traditional intermediaries like banks.", "es": "DeFi (Finanzas Descentralizadas) se refiere a servicios financieros construidos en blockchain que operan sin intermediarios tradicionales como bancos."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is the Byzantine Generals Problem?", "es": "¿Qué es el Problema de los Generales Bizantinos?"}', '[{"en": "A historical battle strategy", "es": "Una estrategia de batalla histórica"}, {"en": "A problem of achieving consensus in distributed systems", "es": "Un problema de lograr consenso en sistemas distribuidos"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A smart contract bug", "es": "Un error de contrato inteligente"}]', 1, '{"en": "The Byzantine Generals Problem describes the challenge of achieving consensus among distributed nodes that may include faulty or malicious participants.", "es": "El Problema de los Generales Bizantinos describe el desafío de lograr consenso entre nodos distribuidos que pueden incluir participantes defectuosos o maliciosos."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is a Merkle tree used for in blockchain?", "es": "¿Para qué se usa un árbol Merkle en blockchain?"}', '[{"en": "To store cryptocurrency", "es": "Para almacenar criptomonedas"}, {"en": "To efficiently verify data integrity", "es": "Para verificar eficientemente la integridad de datos"}, {"en": "To mine blocks", "es": "Para minar bloques"}, {"en": "To create wallets", "es": "Para crear billeteras"}]', 1, '{"en": "A Merkle tree is a data structure that allows efficient verification of data integrity by organizing transaction hashes in a hierarchical tree.", "es": "Un árbol Merkle es una estructura de datos que permite verificación eficiente de integridad de datos organizando hashes de transacciones en un árbol jerárquico."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is the trilemma in blockchain?", "es": "¿Qué es el trilema en blockchain?"}', '[{"en": "Choosing between three cryptocurrencies", "es": "Elegir entre tres criptomonedas"}, {"en": "Trade-off between decentralization, security, and scalability", "es": "Equilibrio entre descentralización, seguridad y escalabilidad"}, {"en": "Three types of wallets", "es": "Tres tipos de billeteras"}, {"en": "Three mining methods", "es": "Tres métodos de minería"}]', 1, '{"en": "The blockchain trilemma refers to the trade-off where optimizing for two of decentralization, security, and scalability often sacrifices the third.", "es": "El trilema de blockchain se refiere al equilibrio donde optimizar dos de descentralización, seguridad y escalabilidad a menudo sacrifica la tercera."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is sharding in blockchain?", "es": "¿Qué es el sharding en blockchain?"}', '[{"en": "Breaking a blockchain into smaller pieces for scalability", "es": "Dividir una blockchain en piezas más pequeñas para escalabilidad"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A wallet backup method", "es": "Un método de respaldo de billetera"}, {"en": "A mining technique", "es": "Una técnica de minería"}]', 0, '{"en": "Sharding divides a blockchain into smaller partitions (shards) that can process transactions in parallel, improving scalability.", "es": "Sharding divide una blockchain en particiones más pequeñas (shards) que pueden procesar transacciones en paralelo, mejorando la escalabilidad."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is a 51% attack?", "es": "¿Qué es un ataque del 51%?"}', '[{"en": "When half the nodes go offline", "es": "Cuando la mitad de los nodos se desconectan"}, {"en": "When an entity controls majority of mining power", "es": "Cuando una entidad controla la mayoría del poder de minería"}, {"en": "A type of wallet hack", "es": "Un tipo de hackeo de billetera"}, {"en": "A smart contract exploit", "es": "Un exploit de contrato inteligente"}]', 1, '{"en": "A 51% attack occurs when an entity controls more than 50% of the networks mining power, potentially allowing them to manipulate the blockchain.", "es": "Un ataque del 51% ocurre cuando una entidad controla más del 50% del poder de minería de la red, potencialmente permitiéndoles manipular la blockchain."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is an oracle in blockchain?", "es": "¿Qué es un oráculo en blockchain?"}', '[{"en": "A fortune teller", "es": "Un adivino"}, {"en": "A service that brings external data to smart contracts", "es": "Un servicio que trae datos externos a contratos inteligentes"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}, {"en": "A consensus mechanism", "es": "Un mecanismo de consenso"}]', 1, '{"en": "A blockchain oracle is a service that provides external, real-world data to smart contracts, enabling them to react to events outside the blockchain.", "es": "Un oráculo de blockchain es un servicio que proporciona datos externos del mundo real a contratos inteligentes, permitiéndoles reaccionar a eventos fuera de la blockchain."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is slashing in Proof of Stake?", "es": "¿Qué es el slashing en Prueba de Participación?"}', '[{"en": "Reducing transaction fees", "es": "Reducir tarifas de transacción"}, {"en": "Penalty for validator misbehavior", "es": "Penalización por mal comportamiento del validador"}, {"en": "A type of reward", "es": "Un tipo de recompensa"}, {"en": "Mining difficulty adjustment", "es": "Ajuste de dificultad de minería"}]', 1, '{"en": "Slashing is a penalty mechanism in PoS where validators lose part of their staked tokens if they act maliciously or fail to perform duties.", "es": "Slashing es un mecanismo de penalización en PoS donde los validadores pierden parte de sus tokens apostados si actúan maliciosamente o no cumplen sus deberes."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is a flash loan?", "es": "¿Qué es un préstamo flash?"}', '[{"en": "A fast bank loan", "es": "Un préstamo bancario rápido"}, {"en": "An uncollateralized loan that must be repaid in the same transaction", "es": "Un préstamo sin colateral que debe pagarse en la misma transacción"}, {"en": "A staking reward", "es": "Una recompensa de staking"}, {"en": "A type of token", "es": "Un tipo de token"}]', 1, '{"en": "A flash loan is a DeFi mechanism where you can borrow any amount without collateral, as long as it is repaid within the same blockchain transaction.", "es": "Un préstamo flash es un mecanismo DeFi donde puedes pedir prestado cualquier cantidad sin colateral, siempre que se pague en la misma transacción de blockchain."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is MEV (Maximal Extractable Value)?", "es": "¿Qué es MEV (Valor Máximo Extraíble)?"}', '[{"en": "A cryptocurrency token", "es": "Un token de criptomoneda"}, {"en": "Profit from reordering or inserting transactions in a block", "es": "Ganancia de reordenar o insertar transacciones en un bloque"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}, {"en": "A consensus mechanism", "es": "Un mecanismo de consenso"}]', 1, '{"en": "MEV is the profit miners/validators can extract by reordering, inserting, or censoring transactions within blocks they produce.", "es": "MEV es la ganancia que los mineros/validadores pueden extraer reordenando, insertando o censurando transacciones dentro de los bloques que producen."}', 'web3-basics'),
('Web3 Basics', 'hard', '{"en": "What is a rollup in Layer 2 scaling?", "es": "¿Qué es un rollup en escalado de Capa 2?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "Bundling many transactions and submitting as one to Layer 1", "es": "Agrupar muchas transacciones y enviarlas como una a Capa 1"}, {"en": "A wallet feature", "es": "Una característica de billetera"}, {"en": "A mining pool", "es": "Un pool de minería"}]', 1, '{"en": "A rollup bundles hundreds of transactions off-chain, then posts the compressed data to Layer 1, inheriting its security while improving throughput.", "es": "Un rollup agrupa cientos de transacciones fuera de cadena, luego publica los datos comprimidos a Capa 1, heredando su seguridad mientras mejora el rendimiento."}', 'web3-basics'),

-- 3. SECURITY
('Security', 'easy', '{"en": "What should you NEVER share with anyone?", "es": "¿Qué NUNCA debes compartir con nadie?"}', '[{"en": "Your public wallet address", "es": "Tu dirección pública de billetera"}, {"en": "Your seed phrase and private keys", "es": "Tu frase semilla y llaves privadas"}, {"en": "Your transaction history", "es": "Tu historial de transacciones"}, {"en": "The cryptocurrencies you own", "es": "Las criptomonedas que posees"}]', 1, '{"en": "Never share your seed phrase or private keys. Anyone with access to these can steal all your crypto assets.", "es": "Nunca compartas tu frase semilla o llaves privadas. Cualquiera con acceso a estos puede robar todos tus activos crypto."}', 'safety'),
('Security', 'easy', '{"en": "What is phishing?", "es": "¿Qué es phishing?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A scam that tricks you into revealing sensitive info", "es": "Una estafa que te engaña para revelar info sensible"}, {"en": "A mining technique", "es": "Una técnica de minería"}, {"en": "A trading strategy", "es": "Una estrategia de trading"}]', 1, '{"en": "Phishing is a scam where attackers create fake websites or messages to trick you into revealing passwords, seed phrases, or other sensitive data.", "es": "Phishing es una estafa donde atacantes crean sitios web o mensajes falsos para engañarte y revelar contraseñas, frases semilla u otros datos sensibles."}', 'safety'),
('Security', 'easy', '{"en": "Where is the safest place to store your seed phrase?", "es": "¿Dónde es más seguro guardar tu frase semilla?"}', '[{"en": "In your email", "es": "En tu correo electrónico"}, {"en": "Written on paper stored in a secure location", "es": "Escrita en papel guardada en un lugar seguro"}, {"en": "In a text file on your computer", "es": "En un archivo de texto en tu computadora"}, {"en": "On social media", "es": "En redes sociales"}]', 1, '{"en": "The safest way is to write it on paper (or metal for fire protection) and store it in a secure physical location like a safe.", "es": "La forma más segura es escribirla en papel (o metal para protección contra fuego) y guardarla en un lugar físico seguro como una caja fuerte."}', 'safety'),
('Security', 'easy', '{"en": "What is a rug pull?", "es": "¿Qué es un rug pull?"}', '[{"en": "A type of NFT", "es": "Un tipo de NFT"}, {"en": "When developers abandon a project and take investor funds", "es": "Cuando desarrolladores abandonan un proyecto y se llevan fondos de inversores"}, {"en": "A trading strategy", "es": "Una estrategia de trading"}, {"en": "A wallet backup", "es": "Un respaldo de billetera"}]', 1, '{"en": "A rug pull is a scam where project developers suddenly withdraw all funds from a liquidity pool, leaving investors with worthless tokens.", "es": "Un rug pull es una estafa donde los desarrolladores del proyecto retiran repentinamente todos los fondos de un pool de liquidez, dejando a inversores con tokens sin valor."}', 'safety'),
('Security', 'easy', '{"en": "What is 2FA?", "es": "¿Qué es 2FA?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "Two-Factor Authentication for extra security", "es": "Autenticación de Dos Factores para seguridad extra"}, {"en": "A trading platform", "es": "Una plataforma de trading"}, {"en": "A wallet type", "es": "Un tipo de billetera"}]', 1, '{"en": "2FA (Two-Factor Authentication) requires two forms of verification to access accounts, adding an extra layer of security.", "es": "2FA (Autenticación de Dos Factores) requiere dos formas de verificación para acceder a cuentas, añadiendo una capa extra de seguridad."}', 'safety'),
('Security', 'medium', '{"en": "What is a hardware wallet?", "es": "¿Qué es una billetera hardware?"}', '[{"en": "A wallet app on your phone", "es": "Una app de billetera en tu teléfono"}, {"en": "A physical device that stores private keys offline", "es": "Un dispositivo físico que almacena llaves privadas sin conexión"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A bank account", "es": "Una cuenta bancaria"}]', 1, '{"en": "A hardware wallet is a physical device that stores your private keys offline, protecting them from online threats like hackers.", "es": "Una billetera hardware es un dispositivo físico que almacena tus llaves privadas sin conexión, protegiéndolas de amenazas en línea como hackers."}', 'safety'),
('Security', 'medium', '{"en": "What is a honeypot scam in crypto?", "es": "¿Qué es una estafa honeypot en crypto?"}', '[{"en": "A type of staking", "es": "Un tipo de staking"}, {"en": "A token designed so you can buy but cannot sell", "es": "Un token diseñado para que puedas comprar pero no vender"}, {"en": "A mining reward", "es": "Una recompensa de minería"}, {"en": "A DeFi protocol", "es": "Un protocolo DeFi"}]', 1, '{"en": "A honeypot is a malicious token contract that allows purchases but prevents sales, trapping your funds. It is designed to look like a legitimate investment.", "es": "Un honeypot es un contrato de token malicioso que permite compras pero previene ventas, atrapando tus fondos. Está diseñado para parecer una inversión legítima."}', 'safety'),
('Security', 'medium', '{"en": "What should you check before connecting your wallet to a dApp?", "es": "¿Qué debes verificar antes de conectar tu billetera a una dApp?"}', '[{"en": "The tokens price", "es": "El precio del token"}, {"en": "The URL and contract permissions", "es": "La URL y los permisos del contrato"}, {"en": "The number of users", "es": "El número de usuarios"}, {"en": "The apps color scheme", "es": "El esquema de colores de la app"}]', 1, '{"en": "Always verify the URL is correct and review what permissions a dApp is requesting before connecting your wallet.", "es": "Siempre verifica que la URL sea correcta y revisa qué permisos está solicitando una dApp antes de conectar tu billetera."}', 'safety'),
('Security', 'medium', '{"en": "What is token approval in crypto?", "es": "¿Qué es la aprobación de token en crypto?"}', '[{"en": "Getting a token listed on an exchange", "es": "Conseguir que un token se liste en un exchange"}, {"en": "Permission for a smart contract to spend your tokens", "es": "Permiso para que un contrato inteligente gaste tus tokens"}, {"en": "A type of airdrop", "es": "Un tipo de airdrop"}, {"en": "A wallet backup", "es": "Un respaldo de billetera"}]', 1, '{"en": "Token approval grants a smart contract permission to spend tokens on your behalf. Always review and revoke unnecessary approvals.", "es": "La aprobación de token otorga a un contrato inteligente permiso para gastar tokens en tu nombre. Siempre revisa y revoca aprobaciones innecesarias."}', 'safety'),
('Security', 'hard', '{"en": "What is a dusting attack?", "es": "¿Qué es un ataque de polvo (dusting)?"}', '[{"en": "Sending large amounts of crypto", "es": "Enviar grandes cantidades de crypto"}, {"en": "Sending tiny amounts of crypto to track wallet activity", "es": "Enviar cantidades diminutas de crypto para rastrear actividad de billetera"}, {"en": "A mining attack", "es": "Un ataque de minería"}, {"en": "A smart contract exploit", "es": "Un exploit de contrato inteligente"}]', 1, '{"en": "A dusting attack involves sending tiny amounts of crypto to many wallets to track and deanonymize users by analyzing transaction patterns.", "es": "Un ataque de polvo implica enviar cantidades diminutas de crypto a muchas billeteras para rastrear y desanonimizar usuarios analizando patrones de transacciones."}', 'safety'),
('Security', 'hard', '{"en": "What is social engineering in crypto scams?", "es": "¿Qué es la ingeniería social en estafas crypto?"}', '[{"en": "Building social media platforms", "es": "Construir plataformas de redes sociales"}, {"en": "Manipulating people psychologically to reveal sensitive information", "es": "Manipular personas psicológicamente para revelar información sensible"}, {"en": "A type of blockchain", "es": "Un tipo de blockchain"}, {"en": "A trading algorithm", "es": "Un algoritmo de trading"}]', 1, '{"en": "Social engineering manipulates people through psychological tactics to reveal passwords, seed phrases, or perform actions that compromise security.", "es": "La ingeniería social manipula personas a través de tácticas psicológicas para revelar contraseñas, frases semilla o realizar acciones que comprometen la seguridad."}', 'safety'),
('Security', 'hard', '{"en": "What is address poisoning?", "es": "¿Qué es el envenenamiento de direcciones?"}', '[{"en": "A type of staking", "es": "Un tipo de staking"}, {"en": "Scammers creating similar-looking addresses to intercept transactions", "es": "Estafadores creando direcciones similares para interceptar transacciones"}, {"en": "A mining technique", "es": "Una técnica de minería"}, {"en": "A wallet feature", "es": "Una característica de billetera"}]', 1, '{"en": "Address poisoning is when scammers send transactions from addresses that look similar to ones you have used, hoping you will copy the wrong address.", "es": "El envenenamiento de direcciones es cuando estafadores envían transacciones desde direcciones que parecen similares a las que has usado, esperando que copies la dirección equivocada."}', 'safety'),

-- 4. DEFI
('DeFi', 'easy', '{"en": "What does DeFi stand for?", "es": "¿Qué significa DeFi?"}', '[{"en": "Digital Finance", "es": "Finanzas Digitales"}, {"en": "Decentralized Finance", "es": "Finanzas Descentralizadas"}, {"en": "Derivative Finance", "es": "Finanzas Derivadas"}, {"en": "Defined Finance", "es": "Finanzas Definidas"}]', 1, '{"en": "DeFi stands for Decentralized Finance, referring to financial services built on blockchain without traditional intermediaries.", "es": "DeFi significa Finanzas Descentralizadas, refiriéndose a servicios financieros construidos en blockchain sin intermediarios tradicionales."}', 'defi-deep-dive'),
('DeFi', 'easy', '{"en": "What is a DEX?", "es": "¿Qué es un DEX?"}', '[{"en": "A centralized exchange", "es": "Un exchange centralizado"}, {"en": "A decentralized exchange for trading crypto", "es": "Un exchange descentralizado para intercambiar crypto"}, {"en": "A digital wallet", "es": "Una billetera digital"}, {"en": "A mining pool", "es": "Un pool de minería"}]', 1, '{"en": "A DEX (Decentralized Exchange) allows users to trade cryptocurrencies directly with each other without a central authority.", "es": "Un DEX (Exchange Descentralizado) permite a los usuarios intercambiar criptomonedas directamente entre sí sin una autoridad central."}', 'defi-deep-dive'),
('DeFi', 'easy', '{"en": "What is liquidity in DeFi?", "es": "¿Qué es la liquidez en DeFi?"}', '[{"en": "Physical water", "es": "Agua física"}, {"en": "Available assets in a pool for trading", "es": "Activos disponibles en un pool para trading"}, {"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "A wallet feature", "es": "Una característica de billetera"}]', 1, '{"en": "Liquidity refers to the availability of assets in a pool that enables trading. Higher liquidity means easier trades with less price impact.", "es": "Liquidez se refiere a la disponibilidad de activos en un pool que permite el trading. Mayor liquidez significa trades más fáciles con menos impacto en precio."}', 'defi-deep-dive'),
('DeFi', 'easy', '{"en": "What is yield farming?", "es": "¿Qué es yield farming?"}', '[{"en": "Growing vegetables", "es": "Cultivar vegetales"}, {"en": "Earning rewards by providing liquidity to DeFi protocols", "es": "Ganar recompensas proporcionando liquidez a protocolos DeFi"}, {"en": "Mining cryptocurrency", "es": "Minar criptomonedas"}, {"en": "Day trading", "es": "Trading diario"}]', 1, '{"en": "Yield farming involves providing assets to DeFi protocols (lending, liquidity pools) in exchange for rewards, often in the form of tokens.", "es": "Yield farming implica proporcionar activos a protocolos DeFi (préstamos, pools de liquidez) a cambio de recompensas, a menudo en forma de tokens."}', 'defi-deep-dive'),
('DeFi', 'medium', '{"en": "What is an AMM?", "es": "¿Qué es un AMM?"}', '[{"en": "A type of cryptocurrency", "es": "Un tipo de criptomoneda"}, {"en": "Automated Market Maker for decentralized trading", "es": "Creador de Mercado Automatizado para trading descentralizado"}, {"en": "A mining machine", "es": "Una máquina de minería"}, {"en": "A wallet provider", "es": "Un proveedor de billeteras"}]', 1, '{"en": "An AMM (Automated Market Maker) uses algorithms and liquidity pools to enable trading without order books, powering DEXs like Uniswap.", "es": "Un AMM (Creador de Mercado Automatizado) usa algoritmos y pools de liquidez para habilitar trading sin libros de órdenes, impulsando DEXs como Uniswap."}', 'defi-deep-dive'),
('DeFi', 'medium', '{"en": "What is impermanent loss?", "es": "¿Qué es la pérdida impermanente?"}', '[{"en": "A permanent loss of funds", "es": "Una pérdida permanente de fondos"}, {"en": "Loss from price changes when providing liquidity", "es": "Pérdida por cambios de precio al proporcionar liquidez"}, {"en": "A trading fee", "es": "Una tarifa de trading"}, {"en": "A type of token", "es": "Un tipo de token"}]', 1, '{"en": "Impermanent loss occurs when the price ratio of tokens in a liquidity pool changes, potentially resulting in less value than simply holding.", "es": "La pérdida impermanente ocurre cuando la relación de precios de tokens en un pool de liquidez cambia, potencialmente resultando en menos valor que simplemente mantener."}', 'defi-deep-dive'),
('DeFi', 'medium', '{"en": "What is TVL in DeFi?", "es": "¿Qué es TVL en DeFi?"}', '[{"en": "Token Value Limit", "es": "Límite de Valor de Token"}, {"en": "Total Value Locked in a protocol", "es": "Valor Total Bloqueado en un protocolo"}, {"en": "Trading Volume Level", "es": "Nivel de Volumen de Trading"}, {"en": "Type of Virtual Ledger", "es": "Tipo de Libro Virtual"}]', 1, '{"en": "TVL (Total Value Locked) measures the total value of assets deposited in a DeFi protocol, indicating its popularity and security.", "es": "TVL (Valor Total Bloqueado) mide el valor total de activos depositados en un protocolo DeFi, indicando su popularidad y seguridad."}', 'defi-deep-dive'),
('DeFi', 'medium', '{"en": "What is collateral in DeFi lending?", "es": "¿Qué es el colateral en préstamos DeFi?"}', '[{"en": "A reward for lending", "es": "Una recompensa por prestar"}, {"en": "Assets you lock to borrow against", "es": "Activos que bloqueas para pedir prestado"}, {"en": "A type of stablecoin", "es": "Un tipo de stablecoin"}, {"en": "A trading fee", "es": "Una tarifa de trading"}]', 1, '{"en": "Collateral is the asset you deposit to secure a loan. In DeFi, loans are often over-collateralized to protect lenders.", "es": "Colateral es el activo que depositas para asegurar un préstamo. En DeFi, los préstamos a menudo están sobre-colateralizados para proteger a los prestamistas."}', 'defi-deep-dive'),
('DeFi', 'hard', '{"en": "What is a liquidity pool?", "es": "¿Qué es un pool de liquidez?"}', '[{"en": "A swimming pool for traders", "es": "Una piscina para traders"}, {"en": "Smart contract holding paired tokens for trading", "es": "Contrato inteligente que mantiene tokens pareados para trading"}, {"en": "A centralized exchange", "es": "Un exchange centralizado"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}]', 1, '{"en": "A liquidity pool is a smart contract containing pairs of tokens that enable decentralized trading through an AMM mechanism.", "es": "Un pool de liquidez es un contrato inteligente que contiene pares de tokens que permiten trading descentralizado a través de un mecanismo AMM."}', 'defi-deep-dive'),
('DeFi', 'hard', '{"en": "What is a vampire attack in DeFi?", "es": "¿Qué es un ataque vampiro en DeFi?"}', '[{"en": "A security exploit", "es": "Un exploit de seguridad"}, {"en": "When a protocol offers incentives to drain liquidity from competitors", "es": "Cuando un protocolo ofrece incentivos para drenar liquidez de competidores"}, {"en": "A type of flash loan", "es": "Un tipo de préstamo flash"}, {"en": "A wallet hack", "es": "Un hackeo de billetera"}]', 1, '{"en": "A vampire attack occurs when a new DeFi protocol offers higher rewards to attract liquidity providers away from an established competitor.", "es": "Un ataque vampiro ocurre cuando un nuevo protocolo DeFi ofrece recompensas más altas para atraer proveedores de liquidez de un competidor establecido."}', 'defi-deep-dive'),
('DeFi', 'hard', '{"en": "What is the difference between simple and compound interest in DeFi?", "es": "¿Cuál es la diferencia entre interés simple y compuesto en DeFi?"}', '[{"en": "Simple is faster", "es": "Simple es más rápido"}, {"en": "Compound includes interest on accumulated interest", "es": "Compuesto incluye interés sobre el interés acumulado"}, {"en": "They are the same", "es": "Son iguales"}, {"en": "Simple is only for NFTs", "es": "Simple es solo para NFTs"}]', 1, '{"en": "Compound interest earns returns on both principal and accumulated interest, while simple interest only earns on the principal.", "es": "El interés compuesto gana rendimientos tanto sobre el capital como sobre el interés acumulado, mientras que el interés simple solo gana sobre el capital."}', 'defi-deep-dive'),
('DeFi', 'hard', '{"en": "What is a governance token?", "es": "¿Qué es un token de gobernanza?"}', '[{"en": "A stablecoin", "es": "Una stablecoin"}, {"en": "A token that gives voting rights in a protocol", "es": "Un token que da derechos de voto en un protocolo"}, {"en": "A mining reward", "es": "Una recompensa de minería"}, {"en": "A wallet address", "es": "Una dirección de billetera"}]', 1, '{"en": "Governance tokens give holders voting rights to participate in protocol decisions, like changing parameters or allocating treasury funds.", "es": "Los tokens de gobernanza dan a los poseedores derechos de voto para participar en decisiones del protocolo, como cambiar parámetros o asignar fondos del tesoro."}', 'defi-deep-dive'),

-- 5. ADVANCED / NFTs / TOKENOMICS
('NFTs', 'easy', '{"en": "What does NFT stand for?", "es": "¿Qué significa NFT?"}', '[{"en": "New Financial Token", "es": "Nuevo Token Financiero"}, {"en": "Non-Fungible Token", "es": "Token No Fungible"}, {"en": "Network File Transfer", "es": "Transferencia de Archivo de Red"}, {"en": "Normal Finance Technology", "es": "Tecnología Financiera Normal"}]', 1, '{"en": "NFT stands for Non-Fungible Token, meaning each token is unique and cannot be replaced by another identical one.", "es": "NFT significa Token No Fungible, lo que significa que cada token es único y no puede ser reemplazado por otro idéntico."}', 'nft-masterclass'),
('NFTs', 'easy', '{"en": "What makes an NFT different from Bitcoin?", "es": "¿Qué hace diferente a un NFT de Bitcoin?"}', '[{"en": "NFTs are more expensive", "es": "Los NFTs son más caros"}, {"en": "Each NFT is unique while each Bitcoin is identical", "es": "Cada NFT es único mientras que cada Bitcoin es idéntico"}, {"en": "NFTs are faster", "es": "Los NFTs son más rápidos"}, {"en": "NFTs dont use blockchain", "es": "Los NFTs no usan blockchain"}]', 1, '{"en": "NFTs are non-fungible (each is unique), while Bitcoin is fungible (each BTC is identical and interchangeable).", "es": "Los NFTs son no fungibles (cada uno es único), mientras que Bitcoin es fungible (cada BTC es idéntico e intercambiable)."}', 'nft-masterclass'),
('NFTs', 'easy', '{"en": "What can an NFT represent?", "es": "¿Qué puede representar un NFT?"}', '[{"en": "Only digital art", "es": "Solo arte digital"}, {"en": "Art, music, gaming items, real estate, and more", "es": "Arte, música, items de juegos, bienes raíces y más"}, {"en": "Only cryptocurrencies", "es": "Solo criptomonedas"}, {"en": "Only physical items", "es": "Solo artículos físicos"}]', 1, '{"en": "NFTs can represent ownership of virtually anything: digital art, music, videos, game items, event tickets, real estate, and more.", "es": "Los NFTs pueden representar propiedad de virtualmente cualquier cosa: arte digital, música, videos, items de juegos, boletos de eventos, bienes raíces y más."}', 'nft-masterclass'),
('NFTs', 'medium', '{"en": "What is minting an NFT?", "es": "¿Qué es mintear un NFT?"}', '[{"en": "Buying an NFT", "es": "Comprar un NFT"}, {"en": "Creating and publishing an NFT on the blockchain", "es": "Crear y publicar un NFT en la blockchain"}, {"en": "Selling an NFT", "es": "Vender un NFT"}, {"en": "Burning an NFT", "es": "Quemar un NFT"}]', 1, '{"en": "Minting is the process of creating a new NFT by publishing it on the blockchain, making it a verifiable digital asset.", "es": "Mintear es el proceso de crear un nuevo NFT publicándolo en la blockchain, convirtiéndolo en un activo digital verificable."}', 'nft-masterclass'),
('NFTs', 'medium', '{"en": "What are royalties in NFTs?", "es": "¿Qué son las regalías en NFTs?"}', '[{"en": "A one-time payment", "es": "Un pago único"}, {"en": "Percentage creators earn from secondary sales", "es": "Porcentaje que ganan los creadores de ventas secundarias"}, {"en": "A type of NFT", "es": "Un tipo de NFT"}, {"en": "A minting fee", "es": "Una tarifa de minteo"}]', 1, '{"en": "Royalties allow NFT creators to automatically receive a percentage of the sale price whenever their NFT is resold on secondary markets.", "es": "Las regalías permiten a los creadores de NFTs recibir automáticamente un porcentaje del precio de venta cada vez que su NFT se revende en mercados secundarios."}', 'nft-masterclass'),
('NFTs', 'hard', '{"en": "What is a PFP NFT?", "es": "¿Qué es un PFP NFT?"}', '[{"en": "A physical NFT", "es": "Un NFT físico"}, {"en": "Profile Picture NFT used as social media avatar", "es": "NFT de Foto de Perfil usado como avatar de redes sociales"}, {"en": "A payment method", "es": "Un método de pago"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}]', 1, '{"en": "PFP (Profile Picture) NFTs are collections designed to be used as social media avatars, like CryptoPunks or Bored Ape Yacht Club.", "es": "Los PFP (Foto de Perfil) NFTs son colecciones diseñadas para usarse como avatares de redes sociales, como CryptoPunks o Bored Ape Yacht Club."}', 'nft-masterclass'),
('Layer 2', 'medium', '{"en": "What is the main purpose of Layer 2 solutions?", "es": "¿Cuál es el propósito principal de las soluciones de Capa 2?"}', '[{"en": "To replace Layer 1", "es": "Reemplazar Capa 1"}, {"en": "To increase speed and reduce costs", "es": "Aumentar velocidad y reducir costos"}, {"en": "To mine cryptocurrency", "es": "Minar criptomonedas"}, {"en": "To create new tokens", "es": "Crear nuevos tokens"}]', 1, '{"en": "Layer 2 solutions process transactions off the main chain to increase throughput and reduce fees while inheriting Layer 1 security.", "es": "Las soluciones de Capa 2 procesan transacciones fuera de la cadena principal para aumentar el rendimiento y reducir tarifas mientras heredan la seguridad de Capa 1."}', NULL),
('Layer 2', 'medium', '{"en": "What is an optimistic rollup?", "es": "¿Qué es un rollup optimista?"}', '[{"en": "A fast trading strategy", "es": "Una estrategia de trading rápida"}, {"en": "L2 that assumes transactions are valid unless challenged", "es": "L2 que asume que las transacciones son válidas a menos que sean impugnadas"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}, {"en": "A mining pool", "es": "Un pool de minería"}]', 1, '{"en": "Optimistic rollups assume transactions are valid by default and only run computations in case of a dispute, during a challenge period.", "es": "Los rollups optimistas asumen que las transacciones son válidas por defecto y solo ejecutan cálculos en caso de disputa, durante un período de impugnación."}', NULL),
('Layer 2', 'hard', '{"en": "What is a ZK-rollup?", "es": "¿Qué es un ZK-rollup?"}', '[{"en": "A centralized exchange", "es": "Un exchange centralizado"}, {"en": "L2 using zero-knowledge proofs to verify transactions", "es": "L2 que usa pruebas de conocimiento cero para verificar transacciones"}, {"en": "A stablecoin", "es": "Una stablecoin"}, {"en": "A wallet feature", "es": "Una característica de billetera"}]', 1, '{"en": "ZK-rollups use zero-knowledge proofs to cryptographically prove transaction validity without revealing details, enabling fast finality.", "es": "Los ZK-rollups usan pruebas de conocimiento cero para probar criptográficamente la validez de transacciones sin revelar detalles, permitiendo finalidad rápida."}', NULL),
('DAOs', 'medium', '{"en": "How do members vote in a DAO?", "es": "¿Cómo votan los miembros en una DAO?"}', '[{"en": "By email", "es": "Por correo electrónico"}, {"en": "Using governance tokens", "es": "Usando tokens de gobernanza"}, {"en": "By phone", "es": "Por teléfono"}, {"en": "There is no voting", "es": "No hay votación"}]', 1, '{"en": "DAO members vote on proposals using governance tokens. More tokens usually means more voting power.", "es": "Los miembros de una DAO votan en propuestas usando tokens de gobernanza. Más tokens usualmente significa más poder de voto."}', NULL),
('DAOs', 'hard', '{"en": "What is a multisig wallet in DAO context?", "es": "¿Qué es una billetera multisig en contexto de DAO?"}', '[{"en": "A wallet for multiple users", "es": "Una billetera para múltiples usuarios"}, {"en": "A wallet requiring multiple signatures to execute transactions", "es": "Una billetera que requiere múltiples firmas para ejecutar transacciones"}, {"en": "A type of hardware wallet", "es": "Un tipo de billetera hardware"}, {"en": "A wallet for NFTs", "es": "Una billetera para NFTs"}]', 1, '{"en": "A multisig wallet requires multiple private key signatures to authorize transactions, providing security for DAO treasuries.", "es": "Una billetera multisig requiere múltiples firmas de llaves privadas para autorizar transacciones, proporcionando seguridad para tesorerías de DAOs."}', NULL),
('Tokenomics', 'medium', '{"en": "What is token vesting?", "es": "¿Qué es el vesting de tokens?"}', '[{"en": "Buying tokens", "es": "Comprar tokens"}, {"en": "Gradual release of tokens over time", "es": "Liberación gradual de tokens a lo largo del tiempo"}, {"en": "Burning tokens", "es": "Quemar tokens"}, {"en": "Staking tokens", "es": "Apostar tokens"}]', 1, '{"en": "Vesting is a schedule that releases tokens gradually over time, preventing immediate selling and aligning long-term incentives.", "es": "Vesting es un cronograma que libera tokens gradualmente a lo largo del tiempo, previniendo venta inmediata y alineando incentivos a largo plazo."}', NULL),
('Tokenomics', 'hard', '{"en": "What is token inflation?", "es": "¿Qué es la inflación de tokens?"}', '[{"en": "Token price increase", "es": "Aumento del precio del token"}, {"en": "Increase in total token supply over time", "es": "Aumento del suministro total de tokens a lo largo del tiempo"}, {"en": "A type of staking", "es": "Un tipo de staking"}, {"en": "A trading strategy", "es": "Una estrategia de trading"}]', 1, '{"en": "Token inflation refers to the rate at which new tokens are created and added to the total supply, potentially diluting existing holders.", "es": "La inflación de tokens se refiere a la tasa a la que se crean nuevos tokens y se añaden al suministro total, potencialmente diluyendo a los poseedores existentes."}', NULL)

ON CONFLICT DO NOTHING;
