INSERT INTO quiz_questions (section_id, question, options, correct_answer, explanation)
VALUES
((SELECT id FROM s1), '{"en": "What defines Web 1.0?", "es": "¿Qué define a Web 1.0?"}', '[{"en": "Read-only static pages", "es": "Páginas estáticas solo lectura"}, {"en": "Social media", "es": "Redes sociales"}, {"en": "Blockchain", "es": "Blockchain"}]', 0, '{"en": "Web 1.0 was read-only with no user interaction.", "es": "Web 1.0 era solo lectura sin interacción."}'),
((SELECT id FROM s1), '{"en": "What is the key feature of Web 2.0?", "es": "¿Cuál es la característica clave de Web 2.0?"}', '[{"en": "Decentralization", "es": "Descentralización"}, {"en": "User-generated content", "es": "Contenido generado por usuarios"}, {"en": "Blockchain", "es": "Blockchain"}]', 1, '{"en": "Web 2.0 enabled users to create content.", "es": "Web 2.0 permitió a usuarios crear contenido."}'),
((SELECT id FROM s1), '{"en": "What makes Web 3.0 different?", "es": "¿Qué hace diferente a Web 3.0?"}', '[{"en": "User ownership", "es": "Propiedad del usuario"}, {"en": "More ads", "es": "Más anuncios"}, {"en": "Faster loading", "es": "Carga más rápida"}]', 0, '{"en": "Web 3.0 gives users ownership of data and assets.", "es": "Web 3.0 da a usuarios propiedad de datos y activos."}'),
((SELECT id FROM s1), '{"en": "Who owns your data in Web 2.0?", "es": "¿Quién posee tus datos en Web 2.0?"}', '[{"en": "You", "es": "Tú"}, {"en": "Platforms like Facebook", "es": "Plataformas como Facebook"}, {"en": "Government", "es": "Gobierno"}]', 1, '{"en": "In Web 2.0, platforms own and monetize your data.", "es": "En Web 2.0, las plataformas poseen y monetizan tus datos."}'),
((SELECT id FROM s1), '{"en": "What is your identity in Web 3.0?", "es": "¿Cuál es tu identidad en Web 3.0?"}', '[{"en": "Your email", "es": "Tu email"}, {"en": "Your wallet address", "es": "Tu dirección de wallet"}, {"en": "Your phone number", "es": "Tu número de teléfono"}]', 1, '{"en": "Your crypto wallet is your Web3 identity.", "es": "Tu wallet es tu identidad Web3."}'),
((SELECT id FROM s1), '{"en": "What decade was Web 1.0?", "es": "¿En qué década fue Web 1.0?"}', '[{"en": "1990s", "es": "1990s"}, {"en": "2000s", "es": "2000s"}, {"en": "2010s", "es": "2010s"}]', 0, '{"en": "Web 1.0 was the internet of the 1990s.", "es": "Web 1.0 fue el internet de los 1990s."}'),
((SELECT id FROM s1), '{"en": "Can content be censored in Web 2.0?", "es": "¿Puede censurarse contenido en Web 2.0?"}', '[{"en": "Yes", "es": "Sí"}, {"en": "No", "es": "No"}, {"en": "Sometimes", "es": "A veces"}]', 0, '{"en": "Platforms can remove any content they want.", "es": "Las plataformas pueden eliminar cualquier contenido."}'),
((SELECT id FROM s1), '{"en": "What is censorship-resistant?", "es": "¿Qué es resistente a censura?"}', '[{"en": "Web 1.0", "es": "Web 1.0"}, {"en": "Web 2.0", "es": "Web 2.0"}, {"en": "Web 3.0", "es": "Web 3.0"}]', 2, '{"en": "Web 3.0 content stored on blockchain cannot be deleted.", "es": "Contenido Web 3.0 en blockchain no puede eliminarse."}'),
((SELECT id FROM s1), '{"en": "How do payments work in Web 3.0?", "es": "¿Cómo funcionan los pagos en Web 3.0?"}', '[{"en": "Through banks", "es": "A través de bancos"}, {"en": "Peer-to-peer", "es": "Peer-to-peer"}, {"en": "Cash only", "es": "Solo efectivo"}]', 1, '{"en": "Web 3.0 enables direct peer-to-peer payments.", "es": "Web 3.0 permite pagos directos peer-to-peer."}'),
((SELECT id FROM s1), '{"en": "Which is an example of Web 2.0 login?", "es": "¿Cuál es ejemplo de login Web 2.0?"}', '[{"en": "MetaMask", "es": "MetaMask"}, {"en": "Login with Google", "es": "Login con Google"}, {"en": "Ledger", "es": "Ledger"}]', 1, '{"en": "Login with Google gives them access to your data.", "es": "Login con Google les da acceso a tus datos."}'),
((SELECT id FROM s1), '{"en": "What is a key problem with Web 2.0?", "es": "¿Cuál es un problema clave de Web 2.0?"}', '[{"en": "Too slow", "es": "Muy lento"}, {"en": "Data breaches and privacy", "es": "Filtraciones y privacidad"}, {"en": "Too expensive", "es": "Muy caro"}]', 1, '{"en": "Centralized data = single point of failure.", "es": "Datos centralizados = punto único de fallo."}'),
((SELECT id FROM s1), '{"en": "What can you own in Web 3.0?", "es": "¿Qué puedes poseer en Web 3.0?"}', '[{"en": "Only money", "es": "Solo dinero"}, {"en": "Data, money, and digital assets", "es": "Datos, dinero y activos digitales"}, {"en": "Nothing", "es": "Nada"}]', 1, '{"en": "Web 3.0 enables ownership of all digital assets.", "es": "Web 3.0 permite propiedad de todos los activos digitales."}'),
((SELECT id FROM s1), '{"en": "Who controls Web 3.0?", "es": "¿Quién controla Web 3.0?"}', '[{"en": "Google", "es": "Google"}, {"en": "Governments", "es": "Gobiernos"}, {"en": "Users collectively", "es": "Usuarios colectivamente"}]', 2, '{"en": "Web 3.0 is decentralized, controlled by users.", "es": "Web 3.0 es descentralizado, controlado por usuarios."}'),
((SELECT id FROM s1), '{"en": "What enables Web 3.0 to work?", "es": "¿Qué permite que Web 3.0 funcione?"}', '[{"en": "Faster servers", "es": "Servidores más rápidos"}, {"en": "Blockchain technology", "es": "Tecnología blockchain"}, {"en": "More data centers", "es": "Más centros de datos"}]', 1, '{"en": "Blockchain is the foundational technology of Web 3.0.", "es": "Blockchain es la tecnología fundamental de Web 3.0."}'),
((SELECT id FROM s1), '{"en": "What is the main promise of Web 3.0?", "es": "¿Cuál es la promesa principal de Web 3.0?"}', '[{"en": "Faster internet", "es": "Internet más rápido"}, {"en": "User sovereignty", "es": "Soberanía del usuario"}, {"en": "Free content", "es": "Contenido gratis"}]', 1, '{"en": "Web 3.0 promises users control over their digital lives.", "es": "Web 3.0 promete a usuarios control de sus vidas digitales."}');
INSERT INTO quiz_questions (section_id, question, options, correct_answer, explanation)
VALUES
((SELECT id FROM s2), '{"en": "What is a blockchain?", "es": "¿Qué es blockchain?"}', '[{"en": "A type of bank", "es": "Un tipo de banco"}, {"en": "A distributed ledger", "es": "Un libro mayor distribuido"}, {"en": "A social network", "es": "Una red social"}]', 1, '{"en": "Blockchain is a distributed ledger shared across many computers.", "es": "Blockchain es un libro mayor distribuido entre muchas computadoras."}'),
((SELECT id FROM s2), '{"en": "Can blockchain data be modified?", "es": "¿Pueden modificarse datos en blockchain?"}', '[{"en": "Yes, easily", "es": "Sí, fácilmente"}, {"en": "No, it is immutable", "es": "No, es inmutable"}, {"en": "Only by admins", "es": "Solo por admins"}]', 1, '{"en": "Once written, blockchain data cannot be changed.", "es": "Una vez escrito, los datos de blockchain no pueden cambiarse."}'),
((SELECT id FROM s2), '{"en": "What are blocks in blockchain?", "es": "¿Qué son los bloques en blockchain?"}', '[{"en": "Physical storage units", "es": "Unidades de almacenamiento físicas"}, {"en": "Containers of transaction data", "es": "Contenedores de datos de transacciones"}, {"en": "Mining equipment", "es": "Equipo de minería"}]', 1, '{"en": "Blocks contain transaction data with timestamps.", "es": "Los bloques contienen datos de transacciones con timestamps."}'),
((SELECT id FROM s2), '{"en": "What links blocks together?", "es": "¿Qué une los bloques?"}', '[{"en": "Cables", "es": "Cables"}, {"en": "Cryptographic hashes", "es": "Hashes criptográficos"}, {"en": "Passwords", "es": "Contraseñas"}]', 1, '{"en": "Each block contains the hash of the previous block.", "es": "Cada bloque contiene el hash del bloque anterior."}'),
((SELECT id FROM s2), '{"en": "What is a node?", "es": "¿Qué es un nodo?"}', '[{"en": "A cryptocurrency", "es": "Una criptomoneda"}, {"en": "A computer in the network", "es": "Una computadora en la red"}, {"en": "A type of wallet", "es": "Un tipo de billetera"}]', 1, '{"en": "Nodes are computers that validate and store blockchain data.", "es": "Los nodos son computadoras que validan y almacenan datos."}'),
((SELECT id FROM s2), '{"en": "What is consensus?", "es": "¿Qué es consenso?"}', '[{"en": "Agreement among nodes", "es": "Acuerdo entre nodos"}, {"en": "A type of crypto", "es": "Un tipo de crypto"}, {"en": "A wallet feature", "es": "Una función de wallet"}]', 0, '{"en": "Consensus is how nodes agree on the state of the blockchain.", "es": "Consenso es cómo los nodos acuerdan el estado de la blockchain."}'),
((SELECT id FROM s2), '{"en": "Advantage of blockchain vs banks?", "es": "¿Ventaja de blockchain vs bancos?"}', '[{"en": "Slower", "es": "Más lento"}, {"en": "Available 24/7", "es": "Disponible 24/7"}, {"en": "More paperwork", "es": "Más papeleo"}]', 1, '{"en": "Blockchain works 24/7 without bank hours or holidays.", "es": "Blockchain funciona 24/7 sin horarios ni feriados."}'),
((SELECT id FROM s2), '{"en": "Who verifies blockchain transactions?", "es": "¿Quién verifica transacciones blockchain?"}', '[{"en": "Banks", "es": "Bancos"}, {"en": "Network nodes", "es": "Nodos de la red"}, {"en": "Government", "es": "Gobierno"}]', 1, '{"en": "Thousands of nodes verify each transaction.", "es": "Miles de nodos verifican cada transacción."}'),
((SELECT id FROM s2), '{"en": "What is transparency in blockchain?", "es": "¿Qué es transparencia en blockchain?"}', '[{"en": "Hidden data", "es": "Datos ocultos"}, {"en": "Anyone can see transactions", "es": "Cualquiera puede ver transacciones"}, {"en": "Private only", "es": "Solo privado"}]', 1, '{"en": "All transactions are publicly visible on the blockchain.", "es": "Todas las transacciones son públicamente visibles."}'),
((SELECT id FROM s2), '{"en": "What is decentralization?", "es": "¿Qué es descentralización?"}', '[{"en": "One central server", "es": "Un servidor central"}, {"en": "No single point of control", "es": "Ningún punto único de control"}, {"en": "Government control", "es": "Control gubernamental"}]', 1, '{"en": "Decentralization means no single entity controls the system.", "es": "Descentralización significa que ninguna entidad controla el sistema."}'),
((SELECT id FROM s2), '{"en": "What is PoW?", "es": "¿Qué es PoW?"}', '[{"en": "Proof of Work", "es": "Prueba de Trabajo"}, {"en": "Power of Wallet", "es": "Poder de Wallet"}, {"en": "Protocol of Web", "es": "Protocolo de Web"}]', 0, '{"en": "Proof of Work is a consensus mechanism using computing power.", "es": "Proof of Work es un mecanismo de consenso usando poder de cómputo."}'),
((SELECT id FROM s2), '{"en": "What is PoS?", "es": "¿Qué es PoS?"}', '[{"en": "Point of Sale", "es": "Punto de Venta"}, {"en": "Proof of Stake", "es": "Prueba de Participación"}, {"en": "Proof of Storage", "es": "Prueba de Almacenamiento"}]', 1, '{"en": "Proof of Stake uses staked tokens instead of computing power.", "es": "Proof of Stake usa tokens apostados en vez de poder de cómputo."}'),
((SELECT id FROM s2), '{"en": "Why is blockchain secure?", "es": "¿Por qué blockchain es seguro?"}', '[{"en": "Strong passwords", "es": "Contraseñas fuertes"}, {"en": "Cryptographic linking", "es": "Enlace criptográfico"}, {"en": "Firewalls", "es": "Firewalls"}]', 1, '{"en": "Changing one block would invalidate all subsequent blocks.", "es": "Cambiar un bloque invalidaría todos los bloques posteriores."}'),
((SELECT id FROM s2), '{"en": "What is a hash?", "es": "¿Qué es un hash?"}', '[{"en": "A password", "es": "Una contraseña"}, {"en": "A unique digital fingerprint", "es": "Una huella digital única"}, {"en": "A wallet address", "es": "Una dirección de wallet"}]', 1, '{"en": "Hashes uniquely identify data and detect any changes.", "es": "Los hashes identifican datos únicamente y detectan cambios."}'),
((SELECT id FROM s2), '{"en": "Is blockchain borderless?", "es": "¿Es blockchain sin fronteras?"}', '[{"en": "No, country-specific", "es": "No, específico por país"}, {"en": "Yes, works globally", "es": "Sí, funciona globalmente"}, {"en": "Only in USA", "es": "Solo en EE.UU."}]', 1, '{"en": "Blockchain works the same everywhere in the world.", "es": "Blockchain funciona igual en todo el mundo."}');
INSERT INTO quiz_questions (section_id, question, options, correct_answer, explanation)
VALUES
((SELECT id FROM s3), '{"en": "What is DeFi?", "es": "¿Qué es DeFi?"}', '[{"en": "Decentralized Finance", "es": "Finanzas Descentralizadas"}, {"en": "Digital Finance", "es": "Finanzas Digitales"}, {"en": "Default Finance", "es": "Finanzas Predeterminadas"}]', 0, '{"en": "DeFi = Decentralized Finance, financial services without banks.", "es": "DeFi = Finanzas Descentralizadas, servicios financieros sin bancos."}'),
((SELECT id FROM s3), '{"en": "What is an NFT?", "es": "¿Qué es un NFT?"}', '[{"en": "New Financial Token", "es": "Nuevo Token Financiero"}, {"en": "Non-Fungible Token", "es": "Token No Fungible"}, {"en": "Network File Transfer", "es": "Transferencia de Archivo de Red"}]', 1, '{"en": "NFT = Non-Fungible Token, a unique digital certificate.", "es": "NFT = Token No Fungible, un certificado digital único."}'),
((SELECT id FROM s3), '{"en": "What is a DEX?", "es": "¿Qué es un DEX?"}', '[{"en": "Decentralized Exchange", "es": "Exchange Descentralizado"}, {"en": "Digital Exchange", "es": "Exchange Digital"}, {"en": "Default Exchange", "es": "Exchange Predeterminado"}]', 0, '{"en": "DEX = Decentralized Exchange, trade without intermediaries.", "es": "DEX = Exchange Descentralizado, intercambia sin intermediarios."}'),
((SELECT id FROM s3), '{"en": "What are stablecoins?", "es": "¿Qué son stablecoins?"}', '[{"en": "Volatile crypto", "es": "Crypto volátil"}, {"en": "Crypto pegged to USD", "es": "Crypto vinculado al USD"}, {"en": "Mining rewards", "es": "Recompensas de minería"}]', 1, '{"en": "Stablecoins maintain stable value, usually pegged to $1.", "es": "Las stablecoins mantienen valor estable, usualmente $1."}'),
((SELECT id FROM s3), '{"en": "What is yield farming?", "es": "¿Qué es yield farming?"}', '[{"en": "Growing vegetables", "es": "Cultivar vegetales"}, {"en": "Earning rewards for providing liquidity", "es": "Ganar recompensas por proveer liquidez"}, {"en": "Mining Bitcoin", "es": "Minar Bitcoin"}]', 1, '{"en": "Provide liquidity to DeFi protocols, earn rewards.", "es": "Provee liquidez a protocolos DeFi, gana recompensas."}'),
((SELECT id FROM s3), '{"en": "What can NFTs represent?", "es": "¿Qué pueden representar los NFTs?"}', '[{"en": "Only art", "es": "Solo arte"}, {"en": "Art, music, tickets, and more", "es": "Arte, música, tickets y más"}, {"en": "Only gaming items", "es": "Solo items de juegos"}]', 1, '{"en": "NFTs can represent any unique digital or physical asset.", "es": "Los NFTs pueden representar cualquier activo único."}'),
((SELECT id FROM s3), '{"en": "How do artists benefit from NFTs?", "es": "¿Cómo benefician los NFTs a artistas?"}', '[{"en": "Free advertising", "es": "Publicidad gratis"}, {"en": "Automatic royalties on resales", "es": "Regalías automáticas en reventas"}, {"en": "More followers", "es": "Más seguidores"}]', 1, '{"en": "Artists receive royalties every time their NFT is resold.", "es": "Artistas reciben regalías cada vez que se revende su NFT."}'),
((SELECT id FROM s3), '{"en": "What is DeFi lending?", "es": "¿Qué son préstamos DeFi?"}', '[{"en": "Bank loans", "es": "Préstamos bancarios"}, {"en": "Lending crypto for interest", "es": "Prestar crypto por interés"}, {"en": "Credit cards", "es": "Tarjetas de crédito"}]', 1, '{"en": "Lend your crypto to earn interest, no banks involved.", "es": "Presta tu crypto para ganar interés, sin bancos."}'),
((SELECT id FROM s3), '{"en": "Who can access DeFi?", "es": "¿Quién puede acceder a DeFi?"}', '[{"en": "Only rich people", "es": "Solo gente rica"}, {"en": "Anyone with internet", "es": "Cualquiera con internet"}, {"en": "Only banks", "es": "Solo bancos"}]', 1, '{"en": "DeFi is permissionless, anyone can participate.", "es": "DeFi no tiene permisos, cualquiera puede participar."}'),
((SELECT id FROM s3), '{"en": "What is a DAO?", "es": "¿Qué es una DAO?"}', '[{"en": "Decentralized Autonomous Organization", "es": "Organización Autónoma Descentralizada"}, {"en": "Digital Art Organization", "es": "Organización de Arte Digital"}, {"en": "Data Access Object", "es": "Objeto de Acceso a Datos"}]', 0, '{"en": "DAOs are community-owned organizations governed by code.", "es": "Las DAOs son organizaciones comunitarias gobernadas por código."}'),
((SELECT id FROM s3), '{"en": "What is gas in Ethereum?", "es": "¿Qué es gas en Ethereum?"}', '[{"en": "Fuel for cars", "es": "Combustible para autos"}, {"en": "Transaction fees", "es": "Tarifas de transacción"}, {"en": "Mining power", "es": "Poder de minería"}]', 1, '{"en": "Gas is the fee paid to execute transactions on Ethereum.", "es": "Gas es la tarifa para ejecutar transacciones en Ethereum."}'),
((SELECT id FROM s3), '{"en": "What is a liquidity pool?", "es": "¿Qué es un pool de liquidez?"}', '[{"en": "A swimming pool", "es": "Una piscina"}, {"en": "Funds locked for trading", "es": "Fondos bloqueados para trading"}, {"en": "A bank account", "es": "Una cuenta bancaria"}]', 1, '{"en": "Liquidity pools enable decentralized trading.", "es": "Los pools de liquidez permiten trading descentralizado."}'),
((SELECT id FROM s3), '{"en": "What is airdrop in crypto?", "es": "¿Qué es airdrop en crypto?"}', '[{"en": "Parachute delivery", "es": "Entrega en paracaídas"}, {"en": "Free token distribution", "es": "Distribución gratuita de tokens"}, {"en": "Dropping prices", "es": "Caída de precios"}]', 1, '{"en": "Airdrops give free tokens to wallet addresses.", "es": "Los airdrops dan tokens gratis a direcciones de wallet."}'),
((SELECT id FROM s3), '{"en": "What is minting an NFT?", "es": "¿Qué es mintear un NFT?"}', '[{"en": "Deleting it", "es": "Eliminarlo"}, {"en": "Creating it on blockchain", "es": "Crearlo en blockchain"}, {"en": "Selling it", "es": "Venderlo"}]', 1, '{"en": "Minting creates a new NFT on the blockchain.", "es": "Mintear crea un nuevo NFT en la blockchain."}'),
((SELECT id FROM s3), '{"en": "What is a smart contract?", "es": "¿Qué es un contrato inteligente?"}', '[{"en": "A legal document", "es": "Un documento legal"}, {"en": "Self-executing code", "es": "Código auto-ejecutable"}, {"en": "A clever agreement", "es": "Un acuerdo inteligente"}]', 1, '{"en": "Smart contracts execute automatically when conditions are met.", "es": "Los contratos inteligentes se ejecutan automáticamente."}');
INSERT INTO quiz_questions (section_id, question, options, correct_answer, explanation)
VALUES
((SELECT id FROM s4), '{"en": "When was the Bitcoin whitepaper published?", "es": "¿Cuándo se publicó el whitepaper de Bitcoin?"}', '[{"en": "2008", "es": "2008"}, {"en": "2010", "es": "2010"}, {"en": "2015", "es": "2015"}]', 0, '{"en": "Satoshi Nakamoto published it in October 2008.", "es": "Satoshi Nakamoto lo publicó en octubre 2008."}'),
((SELECT id FROM s4), '{"en": "Who created Bitcoin?", "es": "¿Quién creó Bitcoin?"}', '[{"en": "Elon Musk", "es": "Elon Musk"}, {"en": "Satoshi Nakamoto", "es": "Satoshi Nakamoto"}, {"en": "Vitalik Buterin", "es": "Vitalik Buterin"}]', 1, '{"en": "Satoshi Nakamoto is the pseudonymous creator of Bitcoin.", "es": "Satoshi Nakamoto es el creador seudónimo de Bitcoin."}'),
((SELECT id FROM s4), '{"en": "When did Ethereum launch?", "es": "¿Cuándo se lanzó Ethereum?"}', '[{"en": "2012", "es": "2012"}, {"en": "2015", "es": "2015"}, {"en": "2018", "es": "2018"}]', 1, '{"en": "Ethereum launched in July 2015.", "es": "Ethereum se lanzó en julio 2015."}'),
((SELECT id FROM s4), '{"en": "What happened in DeFi Summer 2020?", "es": "¿Qué pasó en el verano DeFi 2020?"}', '[{"en": "Bitcoin crashed", "es": "Bitcoin cayó"}, {"en": "DeFi protocols exploded", "es": "Protocolos DeFi explotaron"}, {"en": "Crypto was banned", "es": "Crypto fue prohibido"}]', 1, '{"en": "DeFi protocols saw massive growth and adoption.", "es": "Los protocolos DeFi vieron crecimiento masivo."}'),
((SELECT id FROM s4), '{"en": "When did NFTs go mainstream?", "es": "¿Cuándo se popularizaron los NFTs?"}', '[{"en": "2019", "es": "2019"}, {"en": "2021", "es": "2021"}, {"en": "2023", "es": "2023"}]', 1, '{"en": "2021 saw NFT sales explode with Beeple and others.", "es": "2021 vio las ventas de NFT explotar."}'),
((SELECT id FROM s4), '{"en": "What did Ethereum introduce?", "es": "¿Qué introdujo Ethereum?"}', '[{"en": "Digital gold", "es": "Oro digital"}, {"en": "Smart contracts", "es": "Contratos inteligentes"}, {"en": "Faster payments", "es": "Pagos más rápidos"}]', 1, '{"en": "Ethereum enabled programmable smart contracts.", "es": "Ethereum habilitó contratos inteligentes programables."}'),
((SELECT id FROM s4), '{"en": "What was the ICO boom?", "es": "¿Qué fue el boom de ICOs?"}', '[{"en": "Initial Coin Offerings explosion in 2017", "es": "Explosión de Ofertas Iniciales de Moneda en 2017"}, {"en": "A crypto crash", "es": "Una caída de crypto"}, {"en": "A new coin", "es": "Una nueva moneda"}]', 0, '{"en": "2017 saw thousands of new tokens launched via ICOs.", "es": "2017 vio miles de nuevos tokens lanzados vía ICOs."}'),
((SELECT id FROM s4), '{"en": "Who created Ethereum?", "es": "¿Quién creó Ethereum?"}', '[{"en": "Satoshi Nakamoto", "es": "Satoshi Nakamoto"}, {"en": "Vitalik Buterin", "es": "Vitalik Buterin"}, {"en": "Charlie Lee", "es": "Charlie Lee"}]', 1, '{"en": "Vitalik Buterin proposed Ethereum in 2013.", "es": "Vitalik Buterin propuso Ethereum en 2013."}'),
((SELECT id FROM s4), '{"en": "How much did Beeple NFT sell for?", "es": "¿Por cuánto se vendió el NFT de Beeple?"}', '[{"en": "$6.9 million", "es": "$6.9 millones"}, {"en": "$69 million", "es": "$69 millones"}, {"en": "$690 million", "es": "$690 millones"}]', 1, '{"en": "Beeple sold Everydays for $69 million at Christie.", "es": "Beeple vendió Everydays por $69 millones en Christie."}'),
((SELECT id FROM s4), '{"en": "What is institutional adoption?", "es": "¿Qué es adopción institucional?"}', '[{"en": "Schools using crypto", "es": "Escuelas usando crypto"}, {"en": "Banks and corporations entering crypto", "es": "Bancos y corporaciones entrando a crypto"}, {"en": "Government ban", "es": "Prohibición gubernamental"}]', 1, '{"en": "Large financial institutions investing in crypto.", "es": "Grandes instituciones financieras invirtiendo en crypto."}'),
((SELECT id FROM s4), '{"en": "What is tokenization?", "es": "¿Qué es tokenización?"}', '[{"en": "Creating game tokens", "es": "Crear tokens de juego"}, {"en": "Representing real assets as tokens", "es": "Representar activos reales como tokens"}, {"en": "Breaking tokens", "es": "Romper tokens"}]', 1, '{"en": "Real estate, stocks, and art can become blockchain tokens.", "es": "Inmuebles, acciones y arte pueden ser tokens blockchain."}'),
((SELECT id FROM s4), '{"en": "What are L2 solutions?", "es": "¿Qué son soluciones L2?"}', '[{"en": "New blockchains", "es": "Nuevas blockchains"}, {"en": "Scaling solutions on top of L1", "es": "Soluciones de escalado sobre L1"}, {"en": "Mining hardware", "es": "Hardware de minería"}]', 1, '{"en": "Layer 2 solutions make transactions faster and cheaper.", "es": "Soluciones Layer 2 hacen transacciones más rápidas y baratas."}'),
