((SELECT id FROM s5), '{"en": "Which company uses blockchain for food safety?", "es": "¿Qué empresa usa blockchain para seguridad alimentaria?"}', '[{"en": "Amazon", "es": "Amazon"}, {"en": "Walmart", "es": "Walmart"}, {"en": "Netflix", "es": "Netflix"}]', 1, '{"en": "Walmart tracks food products using blockchain.", "es": "Walmart rastrea productos alimenticios usando blockchain."}'),
((SELECT id FROM s5), '{"en": "How can blockchain help healthcare?", "es": "¿Cómo puede blockchain ayudar en salud?"}', '[{"en": "Cheaper medicine", "es": "Medicina más barata"}, {"en": "Portable medical records", "es": "Registros médicos portables"}, {"en": "More doctors", "es": "Más doctores"}]', 1, '{"en": "Your medical records travel with you securely.", "es": "Tus registros médicos viajan contigo de forma segura."}'),
