((SELECT id FROM s5), '{"en": "How does blockchain help charities?", "es": "¿Cómo ayuda blockchain a organizaciones benéficas?"}', '[{"en": "More donors", "es": "Más donantes"}, {"en": "Transparent fund tracking", "es": "Rastreo transparente de fondos"}, {"en": "Tax benefits", "es": "Beneficios fiscales"}]', 1, '{"en": "Donors can track exactly where their money goes.", "es": "Donantes pueden rastrear exactamente a dónde va su dinero."}'),
((SELECT id FROM s5), '{"en": "What is a real estate use case?", "es": "¿Cuál es un caso de uso en bienes raíces?"}', '[{"en": "Faster construction", "es": "Construcción más rápida"}, {"en": "Tokenized property ownership", "es": "Propiedad tokenizada"}, {"en": "Cheaper rent", "es": "Alquiler más barato"}]', 1, '{"en": "Buy fractional ownership of real estate via tokens.", "es": "Compra propiedad fraccionada de inmuebles vía tokens."}'),
((SELECT id FROM s5), '{"en": "How can blockchain help music artists?", "es": "¿Cómo puede blockchain ayudar a artistas musicales?"}', '[{"en": "More concerts", "es": "Más conciertos"}, {"en": "Direct royalty payments", "es": "Pagos de regalías directos"}, {"en": "Better sound quality", "es": "Mejor calidad de sonido"}]', 1, '{"en": "Artists receive royalties directly without labels.", "es": "Artistas reciben regalías directamente sin sellos."}'),
((SELECT id FROM s5), '{"en": "What is gaming use case for blockchain?", "es": "¿Cuál es un caso de uso en gaming?"}', '[{"en": "Better graphics", "es": "Mejores gráficos"}, {"en": "True ownership of in-game items", "es": "Propiedad real de items del juego"}, {"en": "Faster loading", "es": "Carga más rápida"}]', 1, '{"en": "Own and trade game items across platforms.", "es": "Posee y comercia items de juegos entre plataformas."}'),
((SELECT id FROM s5), '{"en": "How can blockchain prevent diploma fraud?", "es": "¿Cómo puede blockchain prevenir fraude de diplomas?"}', '[{"en": "Digital signatures", "es": "Firmas digitales"}, {"en": "Verifiable on-chain records", "es": "Registros verificables en cadena"}, {"en": "Metal certificates", "es": "Certificados de metal"}]', 1, '{"en": "Diplomas stored on blockchain cannot be faked.", "es": "Diplomas almacenados en blockchain no pueden falsificarse."}'),
((SELECT id FROM s5), '{"en": "What is cross-border payments use case?", "es": "¿Cuál es el caso de uso de pagos transfronterizos?"}', '[{"en": "Sending money internationally in minutes", "es": "Enviar dinero internacionalmente en minutos"}, {"en": "Currency exchange", "es": "Cambio de divisas"}, {"en": "Bank transfers", "es": "Transferencias bancarias"}]', 0, '{"en": "Crypto enables fast, cheap international transfers.", "es": "Crypto permite transferencias internacionales rápidas y baratas."}'),
((SELECT id FROM s5), '{"en": "What is energy trading on blockchain?", "es": "¿Qué es comercio de energía en blockchain?"}', '[{"en": "Selling solar power to neighbors", "es": "Vender energía solar a vecinos"}, {"en": "Mining Bitcoin", "es": "Minar Bitcoin"}, {"en": "Power plants", "es": "Plantas de energía"}]', 0, '{"en": "P2P energy trading between homes with solar panels.", "es": "Comercio P2P de energía entre casas con paneles solares."}'),
((SELECT id FROM s5), '{"en": "How can blockchain help refugees?", "es": "¿Cómo puede blockchain ayudar a refugiados?"}', '[{"en": "Faster travel", "es": "Viajes más rápidos"}, {"en": "Digital identity when documents are lost", "es": "Identidad digital cuando se pierden documentos"}, {"en": "More food", "es": "Más comida"}]', 1, '{"en": "Blockchain provides identity even without physical documents.", "es": "Blockchain provee identidad incluso sin documentos físicos."}');
INSERT INTO quiz_questions (section_id, question, options, correct_answer, explanation)
