((SELECT id FROM s6), '{"en": "What is the salary range for blockchain developers?", "es": "¿Cuál es el rango salarial para desarrolladores blockchain?"}', '[{"en": "$50-80k", "es": "$50-80k"}, {"en": "$150-300k", "es": "$150-300k"}, {"en": "$500k+", "es": "$500k+"}]', 1, '{"en": "Blockchain developers are in high demand.", "es": "Los desarrolladores blockchain tienen alta demanda."}'),
((SELECT id FROM s6), '{"en": "What is a DAO community manager?", "es": "¿Qué es un community manager de DAO?"}', '[{"en": "Someone who manages decentralized community", "es": "Alguien que gestiona comunidad descentralizada"}, {"en": "A banker", "es": "Un banquero"}, {"en": "A lawyer", "es": "Un abogado"}]', 0, '{"en": "DAOs need people to manage their communities.", "es": "Las DAOs necesitan personas para gestionar sus comunidades."}'),
