((SELECT id FROM s7), '{"en": "What is Farcaster?", "es": "¿Qué es Farcaster?"}', '[{"en": "A spell from Harry Potter", "es": "Un hechizo de Harry Potter"}, {"en": "A decentralized social network", "es": "Una red social descentralizada"}, {"en": "A blockchain", "es": "Una blockchain"}]', 1, '{"en": "Farcaster is a decentralized social protocol.", "es": "Farcaster es un protocolo social descentralizado."}'),
((SELECT id FROM s7), '{"en": "What is Lens Protocol?", "es": "¿Qué es Lens Protocol?"}', '[{"en": "Camera app", "es": "App de cámara"}, {"en": "Decentralized social graph", "es": "Grafo social descentralizado"}, {"en": "Photo filter", "es": "Filtro de fotos"}]', 1, '{"en": "Lens lets you own your social connections.", "es": "Lens te permite poseer tus conexiones sociales."}'),
((SELECT id FROM s7), '{"en": "How many wallet users by 2030?", "es": "¿Cuántos usuarios de wallet para 2030?"}', '[{"en": "100 million", "es": "100 millones"}, {"en": "1 billion+", "es": "1 mil millones+"}, {"en": "10 million", "es": "10 millones"}]', 1, '{"en": "Predictions estimate over 1 billion wallet users.", "es": "Las predicciones estiman más de 1 mil millones de usuarios."}'),
((SELECT id FROM s7), '{"en": "What are CBDCs?", "es": "¿Qué son las CBDCs?"}', '[{"en": "Crypto coins", "es": "Monedas crypto"}, {"en": "Central Bank Digital Currencies", "es": "Monedas Digitales de Bancos Centrales"}, {"en": "Credit cards", "es": "Tarjetas de crédito"}]', 1, '{"en": "Government-issued digital currencies.", "es": "Monedas digitales emitidas por gobiernos."}'),
((SELECT id FROM s7), '{"en": "Will gaming use blockchain?", "es": "¿Usarán los juegos blockchain?"}', '[{"en": "Only a few", "es": "Solo algunos"}, {"en": "Most games will have blockchain economies", "es": "La mayoría de juegos tendrá economías blockchain"}, {"en": "None", "es": "Ninguno"}]', 1, '{"en": "Gaming is a major Web3 adoption driver.", "es": "Gaming es un gran impulsor de adopción de Web3."}'),
((SELECT id FROM s7), '{"en": "What is invisible blockchain?", "es": "¿Qué es blockchain invisible?"}', '[{"en": "Hidden transactions", "es": "Transacciones ocultas"}, {"en": "Users dont know theyre using blockchain", "es": "Usuarios no saben que usan blockchain"}, {"en": "Private blockchain", "es": "Blockchain privada"}]', 1, '{"en": "Best UX is when users dont notice the technology.", "es": "La mejor UX es cuando usuarios no notan la tecnología."}'),
((SELECT id FROM s7), '{"en": "What is onchain AI?", "es": "¿Qué es IA onchain?"}', '[{"en": "AI running on blockchain", "es": "IA corriendo en blockchain"}, {"en": "AI trading crypto", "es": "IA comerciando crypto"}, {"en": "AI art", "es": "Arte IA"}]', 0, '{"en": "AI models and agents that live on blockchain.", "es": "Modelos y agentes IA que viven en blockchain."}'),
((SELECT id FROM s7), '{"en": "Will Web3 replace Web2?", "es": "¿Web3 reemplazará a Web2?"}', '[{"en": "Completely", "es": "Completamente"}, {"en": "They will coexist and merge", "es": "Coexistirán y se fusionarán"}, {"en": "Never", "es": "Nunca"}]', 1, '{"en": "Web3 will integrate into existing web, not replace it.", "es": "Web3 se integrará en la web existente, no la reemplazará."}'),
