((SELECT id FROM s8), '{"en": "Do you need coding to use Web3?", "es": "¿Necesitas programar para usar Web3?"}', '[{"en": "Yes, always", "es": "Sí, siempre"}, {"en": "No, just to build it", "es": "No, solo para construirlo"}, {"en": "Yes, for everything", "es": "Sí, para todo"}]', 1, '{"en": "You can use Web3 apps like any regular app.", "es": "Puedes usar apps Web3 como cualquier app normal."}'),
((SELECT id FROM s8), '{"en": "What wallet is recommended for beginners?", "es": "¿Qué wallet se recomienda para principiantes?"}', '[{"en": "Ledger", "es": "Ledger"}, {"en": "MetaMask", "es": "MetaMask"}, {"en": "Paper wallet", "es": "Wallet de papel"}]', 1, '{"en": "MetaMask is free and works in your browser.", "es": "MetaMask es gratis y funciona en tu navegador."}'),
((SELECT id FROM s8), '{"en": "Can you lose money in crypto?", "es": "¿Puedes perder dinero en crypto?"}', '[{"en": "Never", "es": "Nunca"}, {"en": "Yes, if you invest irresponsibly", "es": "Sí, si inviertes irresponsablemente"}, {"en": "Only if hacked", "es": "Solo si te hackean"}]', 1, '{"en": "Crypto is volatile; never invest more than you can lose.", "es": "Crypto es volátil; nunca inviertas más de lo que puedes perder."}'),
((SELECT id FROM s8), '{"en": "Is it too late to start in Web3?", "es": "¿Es tarde para empezar en Web3?"}', '[{"en": "Yes, too late", "es": "Sí, muy tarde"}, {"en": "No, Web3 is still early", "es": "No, Web3 todavía está temprano"}, {"en": "Maybe", "es": "Quizás"}]', 1, '{"en": "Most people have not used Web3 yet. Still early.", "es": "La mayoría no ha usado Web3 aún. Todavía es temprano."}'),
((SELECT id FROM s8), '{"en": "Is Web3 safe?", "es": "¿Es Web3 seguro?"}', '[{"en": "Always safe", "es": "Siempre seguro"}, {"en": "Depends on how you use it", "es": "Depende de cómo lo uses"}, {"en": "Never safe", "es": "Nunca seguro"}]', 1, '{"en": "Learn security basics and start with small amounts.", "es": "Aprende seguridad básica y empieza con montos pequeños."}'),
((SELECT id FROM s8), '{"en": "What is a seed phrase?", "es": "¿Qué es una frase semilla?"}', '[{"en": "A password", "es": "Una contraseña"}, {"en": "12-24 words to recover your wallet", "es": "12-24 palabras para recuperar tu wallet"}, {"en": "Username", "es": "Nombre de usuario"}]', 1, '{"en": "Never share your seed phrase with anyone.", "es": "Nunca compartas tu frase semilla con nadie."}'),
